import React, { Fragment, useState, useEffect } from "react";
import "./App.css";
import DisplayTodo from "./Components/DisplayTodo";
import Form from "./Components/Form";

function App() {
  const [users, setUserList] = useState([]);

  function addUserHandler(userName, userDate) {
    setUserList((prevUserList) => {
      return [
        ...prevUserList,
        { name: userName, date: userDate, id: Math.random().toString() },
      ];
    });
  }
  useEffect(() => {
    const dataSave = localStorage.getItem('saveData');
    if(dataSave) {
      setUserList(JSON.parse(dataSave))
    }
  },[])

  useEffect(() =>{
    localStorage.setItem('saveData',JSON.stringify(users))
  },[])

  return (
    <Fragment>
      <div className="back">
        <h1 className="head">To-DO List</h1>
        <div className="form-box">
          <Form onAddUser={addUserHandler} />
        </div>
      </div>
      <DisplayTodo users={users} />
    </Fragment>
  );
}

export default App;
